	if (project_number == "SREDNA GORA AD(11565)") {
		let testrepottasks = me.sampleTestReportTasks();
		for (let i = 0; i < testrepottasks.getRowCount(); i++) {
			result.AddRow(testrepottasks.rows[i]);
